mapper.update:
update app_process_definition_variables
set process_definition_id = #{processDefinitionId,jdbcType=BIGINT},
  variable_key = #{variableKey,jdbcType=VARCHAR},
  variable_value = #{variableValue,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, process_definition_id, variable_key, variable_value
from app_process_definition_variables
where id = #{id,jdbcType=BIGINT}

select id, process_definition_id, variable_key, variable_value
from app_process_definition_variables
where process_definition_id = #{processDefinitionId}
